import React from 'react';
import Calendar from 'react-calendar';
import TagCloud from 'react-tagcloud';



const SearchBox =()=> {
    return(
        <from action="search.php" method="post">
            <input type="text" title="Search"></input>
            <input type="submit" value="Search"></input>
        </from>
    )
}
const NewsType =()=>{
    return(
        <form>
            <ul>
                <li><label><input type="radio" name="type" value="all">"전체 뉴스"</input></label></li>
                <li><label><input type="radio" name="type" value="korea">"국내 뉴스"</input></label></li>
                <li><label><input type="radio" name="type" value="alien">"세계 뉴스"</input></label></li>
                <li><label><input type="radio" name="type" value="vaccine">"백신 특보"</input></label></li>
            </ul>
        </form>
    )
}
