import React from 'react';
import styled from 'styled-components'
// import TagCloud from 'react-tagcloud';



const SearchBox =()=> {
    return(
        <from action="search.php" method="post">
            <input type="text" title="Search"></input>
            <input type="submit" value="Search"></input>
        </from>
    )
}
const NewsTypeStyle=styled.div`
input type="radio";
name="type";
` 
const NewsType=()=>{
    return(<script>
        <form style={NewsTypeStyle}>
            <ul>
                <li><input>"전체 뉴스"</input></li>
                <li><input value="korea">"국내 뉴스"</input></li>
                <li><input type="radio" name="type" value="alien">"세계 뉴스"</input></li>
                <li><input type="radio" name="type" value="vaccine">"백신 특보"</input></li>
            </ul>
        </form></script>)
}
const DatePeriod = () => {
    return(<div>
        <form>
            <h3>기간 검색</h3>
            <label><input type="date" id="start"/></label>
            <label><input type="date" id="end"/></label>
            <label><input type="button" id="Search" value="Search"/></label>
        </form>    
    </div>)
}

const News = () => {
    return (<>
    <div>
    <table style={{width:"1200px", height:"800px", margin:"0 auto", alignItems:'center center'}}>
            <tr style={{width:"100%", height:"15%"}}>
                <td style={{width:"30%"}}>
                    SPLIT 3 BLOCKS HERE
                <tr><SearchBox />                </tr>
<tr>                <NewsType /></tr>
<tr>                <DatePeriod /></tr>
                {/* <Tags/> */}
                </td>
                <td>LOOP pages HERE</td>
            </tr>
            
        </table>
    </div>
    </>)
}
export default News;
